al-area">							<?php 							$defalt_arg =array('class' => "img-responsive img-circle");							if(has_post_thumbnail()){ ?>							<div class="author-box">								<?php the_post_thumbnail('', $defalt_arg); ?>							</div>							<?php } ?>							<div class="media-body description-box">							<?php if(get_post_meta( get_the_ID(), 'testimonial_role_description', true )){ ?>								<div class="author-description"><?php echo get_post_meta( get_the_ID(), 'testimonial_role_description', true ); ?>								<?php } ?>								</div>								<h4><span></span><?php the_title(); ?><h4>							</div>						</div>						</div>										<?php $i=2;				endwhile; } else {				$default_title = array('Mitchell Doe', 'Anna Culan', 'Rocky Doe');				for($i=1; $i<=3 ; $i++)				{				?>										<div class="col-md-12 pull-left item <?php if($i==1){echo 'active';}?>">						<div class="media testmonial-area">							<div class="author-box">								<img class="img-responsive img-circle" alt="Author" src="<?php echo WEBRITI_TEMPLATE_DIR_URI; ?>/images/testimonial/testi<?php echo $i; ?>.jpg">							</div>							<div class="media-body description-box">								<div class="author-description"><?php _e("Design is a funny word. Some people think design means how it looks. But of course, if you dig deeper, it's really how it works.",'appointment'); ?>								</div>								<h4><span></span><?php echo $default_title[$i-1]; ?>&nbsp;&#45;&nbsp;							</div>						</div>						</div>					<?php } } ?>									</div>												<!-- Testimonial Pagination -->				<div class="row">					<div class="testi-pager">						<ol class="carousel-indicators testi-pagi">						<?php $i=0;						$count_posts = wp_count_posts( 'webriti_testimonial')->publish;						$args = array( 'post_type' => 'webriti_testimonial','posts_per_page' =>$count_posts); 							$testimonial = new WP_Query( $args ); 						if( $testimonial->have_posts() )						{ while ( $testimonial->have_posts() ) : $testimonial->the_post();						?>						<li data-target="#myTestimonial" data-slide-to="<?php echo $i;?>" class="<?php if($i==0){ echo 'active';} ?>"></li>						<?php $i++; endwhile; } else {						?>						<li data-target="#myTestimonial" data-slide-to="0" class="active"></li>						<li data-target="#myTestimonial" data-slide-to="1" ></li>						<li data-target="#myTestimonial" data-slide-to="2" ></li>						<?php } ?>						</ol>					</div>					</div>			</div>			<!-- /Testimonial -->						</div>		</div>	</div><!-- /Testimonial Section --><div class="clearfix"></div><?php } ?>